import styled from "styled-components";

export const HeaderLogin = styled.header`
    background-color: black;
    display: flex;
    justify-content: space-between; /* Alinha os itens à esquerda e à direita */
    align-items: center; /* Alinha os itens verticalmente no centro */
    height: 100%;
`;

export const Title = styled.h1`
    color: white;
    padding-left: 100px;
    padding-top: 20px;
    padding-bottom: 20px;
`;

export const FormsLogin = styled.div``;

export const Subtitle = styled.div`
    color: white;
    display: flex; /* Para permitir que os itens se alinhem lado a lado */
    align-items: center; /* Alinha os itens verticalmente no centro */
    gap: 10px; /* Espaçamento entre o ícone e o texto */
`;

export const Icon = styled.i`
    color: white;
    font-size: 2.0rem;
`;
